from contextlib import ExitStack
from unittest.mock import patch
from uuid import UUID
import xml.etree.ElementTree as etree  # noqa: N813 -- for lxml compatibility

from app.converters import ConversionInput, CsfpfConverter
from app.media_types import MediaTypes
import pendulum
import pytest
from tests.conftest import compare_xml
import time_machine


class TestCsfpfConverter:

    @pytest.mark.asyncio
    async def test_convert(self):
        converter = CsfpfConverter()
        with ExitStack() as stack:
            stack.enter_context(time_machine.travel(pendulum.parse('2020-07-13T17:00:00Z')))
            stack.enter_context(patch('uuid.uuid4', return_value=UUID('a36a2138-2c09-473e-bd0d-916531537b02')))
            result = await converter.convert([ConversionInput(TAC, MediaTypes.TEXT_PLAIN)])

        assert len(result[0].data) > 0
        xml_tree = etree.fromstring(XML)
        result_tree = etree.fromstring(result[0].data)
        assert compare_xml(xml_tree, result_tree)


