import pendulum
import pytest
from tests.conftest import compare_xml
import time_machine


class TestMetarCollectiveConverter:

    @pytest.mark.skip(reason="this test is broken and needs to be fixed")
    @pytest.mark.asyncio
    async def test_convert_metar(self, obs_locator):
        converter = MetarCollectiveConverter(obs_locator)
        with ExitStack() as stack:
            stack.enter_context(time_machine.travel(pendulum.parse('2020-08-13T17:00:00Z')))
            stack.enter_context(patch('uuid.uuid4', return_value=UUID('a36a2138-2c09-473e-bd0d-916531537b02')))
            results = await converter.convert([ConversionInput(TAC_METAR, MediaTypes.TEXT_PLAIN)])

        assert len(results) == 1
        xml_tree = etree.fromstring(XML_METAR)

        result = results[0]
        assert len(result.data) > 0

        result_tree = etree.fromstring(result.data)
        assert compare_xml(xml_tree, result_tree)

